// SPDX-License-Identifier:MIT
pragma solidity 0.8.0;
import  "@openzeppelin/contracts/token/ERC20/ERC20.sol";


contract FBStake {

    uint256 private _Time;
    address private _Customer;
    address private _Provider;
    uint8 private _tokenAmount;
    uint8 private _FBMoneyAmount;
    IERC20 private _token;
    IERC20 private _FBToken;


event StakeCreated(address Customer,uint8 tokenAmount);

modifier AmountNotNull(uint8 amnt) {
    require(amnt!=0);
    _;
}

modifier AddressNotNull(address adr) {
    require(adr!=address(0));
    _;
}

 function getAmount() view external returns(uint256) {
     return _token.balanceOf(_Customer);
 } 


    constructor(address owner, address customer, IERC20 token, uint8 tokenAmount, IERC20 FBToken, uint8 rate)
        AmountNotNull(tokenAmount)
        AddressNotNull(owner)

    {

        _Provider = owner; 
        _Customer = customer;     
        _Time = block.timestamp;
        _token = token;
        _tokenAmount=tokenAmount;
        _FBToken = FBToken;

        // Calculer le montant de conversion du token via ether
        // Effectuer le tansfert  de tokenAmount
        // Effectuer le transfert de FBToken
        // mettre a jour _FBMoneyAmount

        token.transferFrom(_Customer,_Provider, _tokenAmount);
        //emit StakeCreated(msg.sender,tokenAmount);
    }


}
